import React from "react";
import { useState, useContext } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../helpers/AuthContext";

function Login() {
  const [username, setUsername] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [dob, setDob] = useState("");
  const [countryFrom, setCountryFrom] = useState("");
  const [currentCountry, setCurrentCountry] = useState("");
  const [motherTongue, setMotherTongue] = useState("");
  const [profession, setProfession] = useState("");
  const { setAuthState } = useContext(AuthContext);

  let navigate = useNavigate();

  const login = () => {
    if (!mobileNumber) {
      alert("Please enter a mobile number");
      return;
    }

    axios
      .post("http://localhost:3001/login", {
        mobile: mobileNumber,
      })
      .then((response) => {
        console.log(response);
        if (response.data.message) {
          alert(response.data.message);
        } else {
          localStorage.setItem("accessToken", response.data.token);
          setAuthState({
            username: response.data.username,
            id: response.data.id,
            status: true,
          });
          const otp = response.otp
          
          navigate(`/profile/${response.data.id}`);
          //   setUsername(response.data.user.username);
          //   setMobileNumber(response.data.user.mobile);
          //   setDob(response.data.user.dob);
          //   setCountryFrom(response.data.user.countryfrom);
          //   setCurrentCountry(response.data.user.currentcountry);
          //   setMotherTongue(response.data.user.mothertongue);
          //   setProfession(response.data.user.profession);
        }
      });
  };

  return (
    <div>
      <h1>Login</h1>
      {/* Mobile Number */}
      <label>Mobile Number: </label>
      <input
        type="tel"
        onChange={(event) => {
          setMobileNumber(event.target.value);
        }}
      />
      <button onClick={login}>Login</button>
      {/* <div>
        <h1>{username}s</h1>
        <h1>{mobileNumber}</h1>
        <h1>{dob}</h1>
        <h1>{countryFrom}</h1>
        <h1>{currentCountry}</h1>
        <h1>{motherTongue}</h1>
        <h1>{profession}</h1>
      </div> */}
    </div>
  );
}

export default Login;
